import { useEffect, useState } from "react";
import axios from "axios";
import PlannerForm from "./components/PlannerForm";
import PlannerTable from "./components/PlannerTable";
import "./App.css";

function App() {

  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch plans from backend (dynamic)
  const fetchPlans = async () => {
    try {
      setLoading(true);

      const res = await axios.get("http://localhost:5000/plans");

      setPlans(res.data);

      setError("");

    } catch (err) {

      console.error(err);

      setError("Failed to fetch plans. Make sure backend is running.");

    } finally {

      setLoading(false);

    }
  };

  // Run when app loads
  useEffect(() => {
    fetchPlans();
  }, []);

  return (
    <div className="container">

      <h1>AI Smart Study Planner</h1>

      {/* Form */}
      <PlannerForm refresh={fetchPlans} />

      {/* Loading */}
      {loading && <p>Loading plans...</p>}

      {/* Error */}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Table */}
      {!loading && !error && (
        <PlannerTable plans={plans} />
      )}

    </div>
  );
}

export default App;
